#include <bits/stdc++.h>using namespace std;ifstream f("inv.in"); ofstream g("inv.out");#define ub(x) (x&(-x))#define MOD 9917int n,i,p,nr,AIB[100005],x;long long inv;vector <pair <int,int> >v;long long sol;void add(int x){	for(int j=x; j<=n; j+=ub(j)) AIB[j]++; }long long sum(int x){	long long s=0;    for(int j=x; j>0; j-=ub(j)) s+=AIB[j];    return s;}int main(){	f>>n;	for(i=0;i<n;i++)	{	f>>x;		v.push_back(make_pair(x,i+1));	}	sort(v.begin(),v.end());	inv=1LL*n*(n-1)/2;	for(i=0; i<n; ++i)    {	x=v[i].second;        inv-=sum(x);        add(x);    }	g<<inv%9917;    g.close(); return 0;}
